import React, { useRef } from 'react';
import { Link } from 'react-scroll';
import './App.css';

function App() {
  const pageRef = useRef(null);

  const scrollToPage = (pageId) => {
    const page = document.getElementById(pageId);
    if (pageRef.current && page) {
      pageRef.current.scrollTo({
        left: page.offsetLeft,
        behavior: 'smooth',
      });
    }
  };

  return (
    <div className="App">
      <div className="page-container" ref={pageRef}>
        <div className="page" id="home">
          <h1>Home Page</h1>
          {/* Add content for Home Page */}
        </div>
        <div className="page" id="forest">
          <h1>Forest Page</h1>
          {/* Add content for Forest Page */}
        </div>
        <div className="page" id="beach">
          <h1>Beach Page</h1>
          {/* Add content for Beach Page */}
        </div>
        <div className="page" id="urban">
          <h1>Urban Page</h1>
          {/* Add content for Urban Page */}
        </div>
      </div>

      <div className="nav-links bottom">
        <button onClick={() => scrollToPage('home')}>Home</button>
        <button onClick={() => scrollToPage('forest')}>Forest</button>
        <button onClick={() => scrollToPage('beach')}>Beach</button>
        <button onClick={() => scrollToPage('urban')}>Urban</button>
      </div>
    </div>
  );
}

export default App;
