import React, { useRef, useState } from 'react';
import './App.css';

function App() {
  const [soundPlaying, setSoundPlaying] = useState({
    Fan: false,
    Cat: false,
    Clock: false,
    Television: false,
    AC: false,
    Rain: false,
    Thunder: false,
    Birds: false,
    River: false,
    Wind: false,
    Waves: false,
    Seagulls: false,
    Boat: false,
    Campfire: false,
    Melody: false,
    People: false,
    Traffic: false,
    Pigeons: false,
    Truck: false,
    Train: false,
    // Add other sound states similarly for each sound
  });

  const audioRefs = {};

  const pageRef = useRef(null);

  const scrollToPage = (pageId) => {
    const page = document.getElementById(pageId);
    if (pageRef.current && page) {
      pageRef.current.scrollTo({
        left: page.offsetLeft,
        behavior: 'smooth',
      });
    }
  };

  const playOrStopSound = (sound) => {
    const audioPath = `${process.env.PUBLIC_URL}/sounds/${sound}.mp3`;
    console.log('Audio Path:', audioPath);

    const audio = new Audio(audioPath);

    if (soundPlaying[sound]) {
      audio.volume = 0;
      audio.pause();
      audio.currentTime = 0;
      setSoundPlaying((prevState) => ({
        ...prevState,
        [sound]: false,
      }));
    } else {
      audio.volume = 1;
      audio.play().catch((error) => {
        console.error('Failed to play audio:', error);
      });
      setSoundPlaying((prevState) => ({
        ...prevState,
        [sound]: true,
      }));
    }
  };

  return (
    <div className="App">
      <div className="page-container" ref={pageRef}>
        <div className="page home" id="home">
          <h1>Home Page</h1>
          <div className="sound-tiles">
            <div className="tile" onClick={() => playOrStopSound('Fan')}>Fan</div>
            <div className="tile" onClick={() => playOrStopSound('Cat')}>Cat</div>
            <div className="tile" onClick={() => playOrStopSound('Clock')}>Clock</div>
            <div className="tile" onClick={() => playOrStopSound('Television')}>Television</div>
            <div className="tile" onClick={() => playOrStopSound('AC')}>AC</div>
          </div>
        </div>

        <div className="page forest" id="forest">
          <h1>Forest Page</h1>
          <div className="sound-tiles">
            <div className="tile" onClick={() => playOrStopSound('Rain')}>Rain</div>
            <div className="tile" onClick={() => playOrStopSound('Thunder')}>Thunder</div>
            <div className="tile" onClick={() => playOrStopSound('Birds')}>Birds</div>
            <div className="tile" onClick={() => playOrStopSound('River')}>River</div>
            <div className="tile" onClick={() => playOrStopSound('Wind')}>Wind</div>
          </div>
        </div>

        <div className="page beach" id="beach">
          <h1>Beach Page</h1>
          <div className="sound-tiles">
            <div className="tile" onClick={() => playOrStopSound('Waves')}>Waves</div>
            <div className="tile" onClick={() => playOrStopSound('Seagulls')}>Seagulls</div>
            <div className="tile" onClick={() => playOrStopSound('Boat')}>Boat</div>
            <div className="tile" onClick={() => playOrStopSound('Campfire')}>Campfire</div>
            <div className="tile" onClick={() => playOrStopSound('Melody')}>Melody</div>
          </div>
        </div>

        <div className="page urban" id="urban">
          <h1>Urban Page</h1>
          <div className="sound-tiles">
            <div className="tile" onClick={() => playOrStopSound('People')}>People</div>
            <div className="tile" onClick={() => playOrStopSound('Traffic')}>Traffic</div>
            <div className="tile" onClick={() => playOrStopSound('Pigeons')}>Pigeons</div>
            <div className="tile" onClick={() => playOrStopSound('Truck')}>Truck</div>
            <div className="tile" onClick={() => playOrStopSound('Train')}>Train</div>
          </div>
        </div>
      </div>

      <div className="nav-links bottom">
        <button onClick={() => scrollToPage('forest')}>Forest</button>
        <button onClick={() => scrollToPage('beach')}>Beach</button>
        <button onClick={() => scrollToPage('urban')}>Urban</button>
        <button onClick={() => scrollToPage('home')}>Home</button>
      </div>
    </div>
  );
}

export default App;
