import React, { useRef, useState, useEffect } from 'react';
import './App.css';

function App() {
  const [soundPlaying, setSoundPlaying] = useState({
    Fan: false,
    Cat: false,
    Clock: false,
    Television: false,
    AC: false,
    Rain: false,
    Thunder: false,
    Birds: false,
    River: false,
    Wind: false,
    Waves: false,
    Seagulls: false,
    Boat: false,
    Campfire: false,
    Melody: false,
    People: false,
    Traffic: false,
    Pigeons: false,
    Truck: false,
    Train: false,
    // Add other sound states similarly for each sound
  });

  const [activeSound, setActiveSound] = useState(null);

  const audioRefs = useRef({});

  const pageRef = useRef(null);

  const scrollToPage = (pageId) => {
    const page = document.getElementById(pageId);
    if (pageRef.current && page) {
      pageRef.current.scrollTo({
        left: page.offsetLeft,
        behavior: 'smooth',
      });
    }
  };

  const stopAllSounds = (currentSound) => {
    Object.keys(audioRefs.current).forEach((sound) => {
      if (sound !== currentSound && !audioRefs.current[sound].paused) {
        audioRefs.current[sound].pause();
        audioRefs.current[sound].currentTime = 0;
      }
    });
  };

  const playOrStopSound = (sound) => {
    const audioPath = `${process.env.PUBLIC_URL}/sounds/${sound}.mp3`;

    if (soundPlaying[sound]) {
      audioRefs.current[sound].pause();
      audioRefs.current[sound].currentTime = 0;
      setSoundPlaying((prevState) => ({ ...prevState, [sound]: false }));
      setActiveSound(null);
    } else {
      stopAllSounds(sound);
      audioRefs.current[sound] = new Audio(audioPath);
      audioRefs.current[sound].play().catch((error) => {
        console.error('Failed to play audio:', error);
      });
      setSoundPlaying((prevState) => ({ ...prevState, [sound]: true }));
      setActiveSound(sound);
    }
  };

  useEffect(() => {
    return () => {
      Object.keys(audioRefs.current).forEach((sound) => {
        if (!audioRefs.current[sound].paused) {
          audioRefs.current[sound].pause();
          audioRefs.current[sound].currentTime = 0;
        }
      });
    };
  }, []);

  return (
    <div className="App">
      <div className="page-container" ref={pageRef}>
        {/* Your page divs here */}
        <div className="page home" id="home">
          <h1>Home Page</h1>
          <div className="sound-tiles">


            <div className={`tile ${activeSound === 'Fan' ? 'active' : ''}`} onClick={() => playOrStopSound('Fan')}>Fan</div>
            <div className={`tile ${activeSound === 'Cat' ? 'active' : ''}`} onClick={() => playOrStopSound('Cat')}>Cat</div>
            <div className={`tile ${activeSound === 'Clock' ? 'active' : ''}`} onClick={() => playOrStopSound('Clock')}>Clock</div>
            <div className={`tile ${activeSound === 'Television' ? 'active' : ''}`} onClick={() => playOrStopSound('Television')}>Television</div>
            <div className={`tile ${activeSound === 'AC' ? 'active' : ''}`} onClick={() => playOrStopSound('AC')}>AC</div>
          </div>
        </div>

        <div className="page forest" id="forest">
        <div className="page forest" id="forest" style={{ backgroundImage: 'url("./forest.jpg")' }}></div>  
          <h1>Forest Page</h1>
          <div className="sound-tiles">
          <div className={`tile ${activeSound === 'Rain' ? 'active' : ''}`} onClick={() => playOrStopSound('Rain')}>Rain</div>
          <div className={`tile ${activeSound === 'Thunder' ? 'active' : ''}`} onClick={() => playOrStopSound('Thunder')}>Thunder</div>  
          <div className={`tile ${activeSound === 'Birds' ? 'active' : ''}`} onClick={() => playOrStopSound('Birds')}>Birds</div>
          <div className={`tile ${activeSound === 'River' ? 'active' : ''}`} onClick={() => playOrStopSound('River')}>River</div>
          <div className={`tile ${activeSound === 'Wind' ? 'active' : ''}`} onClick={() => playOrStopSound('Wind')}>Wind</div>
          </div>
        </div>

        <div className="page beach" id="beach">
          <h1>Beach Page</h1>
          <div className="sound-tiles">
          <div className={`tile ${activeSound === 'Waves' ? 'active' : ''}`} onClick={() => playOrStopSound('Waves')}>Waves</div>
          <div className={`tile ${activeSound === 'Seagulls' ? 'active' : ''}`} onClick={() => playOrStopSound('Seagulls')}>Seagulls</div>
          <div className={`tile ${activeSound === 'Boat' ? 'active' : ''}`} onClick={() => playOrStopSound('Boat')}>Boat</div>
          <div className={`tile ${activeSound === 'Campfire' ? 'active' : ''}`} onClick={() => playOrStopSound('Campfire')}>Campfire</div>
          <div className={`tile ${activeSound === 'Melody' ? 'active' : ''}`} onClick={() => playOrStopSound('Melody')}>Melody</div>
          </div>
        </div>

        <div className="page urban" id="urban">
          <h1>Urban Page</h1>
          <div className="sound-tiles">
          <div className={`tile ${activeSound === 'People' ? 'active' : ''}`} onClick={() => playOrStopSound('People')}>People</div>
          <div className={`tile ${activeSound === 'Traffic' ? 'active' : ''}`} onClick={() => playOrStopSound('Traffic')}>Trafic</div>
          <div className={`tile ${activeSound === 'Pigeons' ? 'active' : ''}`} onClick={() => playOrStopSound('Pigeons')}>Pigeons</div>
          <div className={`tile ${activeSound === 'Truck' ? 'active' : ''}`} onClick={() => playOrStopSound('Truck')}>Truck</div>
          <div className={`tile ${activeSound === 'Train' ? 'active' : ''}`} onClick={() => playOrStopSound('Train')}>Train</div>
          </div>
        </div>
      </div>

      <div className="nav-links bottom">
        <button onClick={() => scrollToPage('forest')}>Forest</button>
        <button onClick={() => scrollToPage('beach')}>Beach</button>
        <button onClick={() => scrollToPage('urban')}>Urban</button>
        <button onClick={() => scrollToPage('home')}>Home</button>
      </div>
    </div>
  );
}

export default App;
