import React, { useRef } from 'react';
import './App.css';

function App() {
  const pageRef = useRef(null);

  const scrollToPage = (pageId) => {
    const page = document.getElementById(pageId);
    if (pageRef.current && page) {
      pageRef.current.scrollTo({
        left: page.offsetLeft,
        behavior: 'smooth',
      });
    }
  };

  const playSound = (sound) => {
    const audio = new Audio(`/sounds/${sound}.mp3`); // Assumes mp3 files in the 'sounds' folder
    audio.play();
  };

  return (
    <div className="App">
      <div className="page-container" ref={pageRef}>
        <div className="page home" id="home">
          <h1>Home Page</h1>
          <div className="sound-tiles">
            <div className="tile" onClick={() => playSound('Fan')}>Fan</div>
            <div className="tile" onClick={() => playSound('Cat')}>Cat</div>
            <div className="tile" onClick={() => playSound('Clock')}>Clock</div>
            <div className="tile" onClick={() => playSound('Television')}>Television</div>
            <div className="tile" onClick={() => playSound('AC')}>AC</div>
          </div>
        </div>
        {/* Other pages with sound tiles */}
      </div>
      <div className="nav-links bottom">
        <button onClick={() => scrollToPage('forest')}>Forest</button>
        <button onClick={() => scrollToPage('beach')}>Beach</button>
        <button onClick={() => scrollToPage('urban')}>Urban</button>
        <button onClick={() => scrollToPage('home')}>Home</button>
      </div>
    </div>
  );
}

export default App;
